import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();


    this.state = {
      monsters: [ //we are creating an array
        {
          name: 'Frankenstein'
        }
      ] 
    };
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            {this.state.string}
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a> <br/>

          <button onClick={() => this.setState({string: 'Hello my love'})}>change text</button>
        </header>
    </div>
    )
  }
}


export default App;
