import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      //we are creating an array here
      monsters: [ 
        {
          name: 'Frankenstein',
          id: 'asr1'
        },
        {
          name: 'Dracula',
          id: 'asr2'
        },
        {
          name: 'Zombie',
          id: 'asr3'
        }
      ] 
    };
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(Response.json())
  };

  render() {
    return (
      <div className="App">
       {//the key prop lets react know to just render what gets updated not all items. 
         this.state.monsters.map(monster => <h1 key={monster.id}> { monster.name } </h1>)
       }
    </div>
    )
  }
}


export default App;
