import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();


    this.state = {
      //we are creating an array here
      monsters: [ 
        {
          name: 'Frankenstein'
        },
        {
          name: 'Dracula'
        },
        {
          name: 'Zombie'
        }
      ] 
    };
  }
  render() {
    return (
      <div className="App">
       {
         this.state.monsters.map()
       }
    </div>
    )
  }
}


export default App;
