import React, { Component } from 'react';

import { CardList } from './components/card-list'

import './App.css';

class App extends Component {
  constructor() {
    super();

    this.state = {
      //we are creating an array here
      monsters: [] //nomrally would not hard code data here we would fetch from an api
    };
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(response => response.json())
    .then(users => this.setState({ monsters: users}));//users was the file name in the jsonplaceholder uptop
  }

  render() {
    return (
      <div className="App">
       {//the key prop lets react know to just render what gets updated not all items. 
         this.state.monsters.map(monster => <h1 key={monster.id}> { monster.name } </h1>)
       }
    </div>
    )
  }
}


export default App;
